mapper.select:


SELECT * FROM (
SELECT
A.*,
@rownum := @rownum + 1 AS r
FROM
(SELECT * FROM
BOARD where cat_no =1 ORDER BY b_no DESC) A,
(SELECT
@rownum := 0) R
) B
WHERE r BETWEEN #{start} AND #{end}

SELECT * FROM (
SELECT A.*, @rownum := @rownum + 1 AS r
FROM
(SELECT * FROM BOARD WHERE cat_no = 1 AND topic_no = #{topicNo} ORDER
BY b_no DESC) A,
(SELECT @rownum := 0) R
) B WHERE r BETWEEN #{start}
AND #{end}

mapper.update:

update BOARD set
b_title=#{b_title},
b_content=#{b_content}
where b_no =
#{b_no}
