mapper.select:
SELECT B.B_TITLE, B.B_CONTENT, B.B_WRITE_DATE, B.B_VIEW, B.B_REPLY, B.B_RECOMMEND, B.MEMBER_NO, B.CAT_NO, M.*
FROM BOARD B INNER JOIN MEETING M ON B.B_NO = M.B_NO
WHERE B.B_NO = #{boardIdx};

mapper.insert:
insert INTO BOARD(b_no,
b_title, b_content, b_write_date, member_no, cat_no)
VALUES((SELECT
t.b_no+1 FROM (SELECT MAX(b_no) b_no FROM BOARD) AS t)
,#{b_title}
,#{b_content}
,CURDATE()
,#{member_no}
,3
)

insert INTO MEETING(b_no, meet_start_day, meet_end_day,
meet_tags, meet_topic_name, meet_state, meet_field, backend, frontend,
server, pm)
VALUES (
(SELECT
t.b_no FROM (SELECT MAX(b_no) b_no FROM
BOARD) AS t)
,#{meet_start_day}
,#{meet_end_day}
,#{meet_tags}
,#{meet_topic_name}
,'모집 중'
,#{meet_field}
,#{backend}
,#{frontend}
,#{server}
,#{pm}
)

mapper.select:
SELECT * FROM REPLY
WHERE b_no = #{boardIdx}
ORDER by
reply_no desc;
